import { useRef , useEffect } from 'react'

export {useRef} from 'react'
const Banner_Circle_Animation = (props) => {
    const canvasRef = useRef(null)
    const canvasSize = {
        width: 250,
        height: 250
    }
    // partical
    function Particle (x, y, radius, color, context){
        this.x = x;
        this.y = y;
        this.radius = radius;
        this.color  = color;
        this.radians = Math.random() * Math.PI * 2;
        this.velocity = 0.05;
        this.distanceFromCenter =  {
            x: Math.random() * 20 + 50,
            y: Math.random() * 20 + 50,
        }
        this.update = () => {
            this.radians += this.velocity;

            this.x = x + Math.cos(this.radians) * this.distanceFromCenter.x;

            this.y = y + Math.sin(this.radians) * this.distanceFromCenter.y;
            this.draw()
        }
        this.draw = () => {
            context.beginPath();
            context.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false)
            context.fillStyle = this.color;
            context.fill();
            context.closePath();
        }
    };
    let particals;
    const Init = (canvas) => {
        particals = []
        for(let i = 0; i < 15; i++) {
            particals.push(new Particle(250 / 2, 250/2, 5, "blue", canvas));
        }
    }
    const Animate = (canvas) => {
        canvas.fillStyle = 'rgba(255, 255, 255, 0.05)'
        canvas.fillRect(0, 0, canvas.width, canvas.height);
        particals.forEach(partical => {
            console.log(partical.radians)
            partical.update()
        })
        requestAnimationFrame(() => {
            Animate(canvas)
            })
       
    }
    useEffect(() => {
        const canvas = canvasRef.current;
        canvas.width = canvasSize.width;
        canvas.height = canvasSize.height
        const ctx = canvas.getContext('2d');
        Init(ctx)
        Animate(ctx)
    },[])
    return (
        <canvas ref = {canvasRef}></canvas>
    )
}
export default Banner_Circle_Animation
