import { useRef , useEffect } from 'react'

export {useRef} from 'react'
const Banner_Circle_Animation = (props) => {
    const canvasRef = useRef(null)
    const canvasSize = {
        width: 250,
        height: 250
    }
    // color
    const color = [
        '#e0a80d',
        '#bde00d',
        '#53e00d',
        '#0d26e0',
        '#7e0de0',
        '#e0a80d',
        '#bde00d',
        '#53e00d',
        '#0d26e0',
        '#7e0de0'
    ]
    let mouse = {
        x : undefined,
        y : undefined
    } 
    // partical
    function Particle (x, y, radius, color, context){
        this.x = x;
        this.y = y;
        this.radius = radius;
        this.color  = color;
        this.radians = Math.random() * Math.PI * 2;
        this.velocity = 0.04;
        this.distanceFromCenter =  {
            x: Math.random() * 10 + 50,
            y: Math.random() * 10 + 50,
        }
        this.update = () => {
            this.radians += this.velocity;

            this.x = (mouse.x === undefined ? x : mouse.x) + Math.cos(this.radians) * this.distanceFromCenter.x;

            this.y = (mouse.y === undefined ? x : mouse.y) + Math.sin(this.radians) * this.distanceFromCenter.y;
            this.draw()
        }
        this.draw = () => {
            context.beginPath();
            context.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false)
            context.fillStyle = this.color;
            context.fill();
            context.closePath();
        }
    };
    let particals;
    const Init = (canvas) => {
        particals = []
        for(let i = 0; i < 10; i++) {
            particals.push(new Particle(250 / 2, 250/2, 10, color[i], canvas));
        }
    }
    const Animate = (ctx) => {
       
        ctx.fillStyle = 'rgba(0,0,0,0.1)'
        ctx.fillRect(0, 0, canvasSize.width, canvasSize.height);
        particals.forEach(partical => {
            partical.update()
        })
        requestAnimationFrame(() => {
           
            Animate(ctx)
            })
       
    }
    useEffect(() => {
        
        const canvas = canvasRef.current;
        window.addEventListener('mousemove', (e) => {
            if(e.x < canvas.getBoundingClientRect().left||
                e.x ) {
                console.log("ok")
                mouse.x = undefined;
                mouse.y = undefined;
            }
        }
            
        )
        canvas.addEventListener('mousemove', (e) => {
            console.log(canvas.left)
            console.log(mouse)
            mouse.x = e.x - canvas.getBoundingClientRect().left;
            mouse.y = e.y - canvas.getBoundingClientRect().top;
        })
        canvas.width = canvasSize.width;
        canvas.height = canvasSize.height
        const ctx = canvas.getContext('2d');
        Init(ctx)
        Animate(ctx)
    },[])
    return (
        <canvas ref = {canvasRef}></canvas>
    )
}
export default Banner_Circle_Animation
