import { useRef , useEffect } from 'react'

export {useRef} from 'react'
const Banner_Circle_Animation = (props) => {
    const canvasRef = useRef(null)
    const canvasSize = {
        width: 250,
        height: 250
    }
    // partical
    function Particle (x, y, radius, color, context){
        this.x = x;
        this.y = y;
        this.radius = radius;
        this.color  = color;
        this.update = () => {
            this.draw()
        }
        this.draw = () => {
            context.beginPath();
            context.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false)
            context.fillStyle = this.color;
            context.fill();
            context.closePath();
        }
    };
    let particals;
    const Init = (canvas) => {
        particals = []
        for(let i = 0; i < 1; i++) {
            particals.push(new Particle(250 / 2, 250 / 2, canvas.height/2, "blue", canvas));
        }
        console.log(particals)
    }
    const Animate = (canvas) => {
        particals.forEach(partical => {
            partical.update()
            console.log("ok")
        })
       
       
    }
    useEffect(() => {
        const canvas = canvasRef.current;
        canvas.width = canvasSize.width;
        canvas.height = canvasSize.height
        const ctx = canvas.getContext('2d');
        Init(ctx)
        requestAnimationFrame(() => {
            Animate(ctx)
        })
    },[])
    return (
        <canvas ref = {canvasRef}></canvas>
    )
}
export default Banner_Circle_Animation
